    },
    components: {
        'Tree': Tree,
        'b-modal': bModal
    },
    directives: {
        'b-modal': bModalDirective
    },
    mounted() {
        //console.log(LOCAL_URLS.QUESTION.CREATE);
    },
    updated() {
       
    },
    methods: {
        doOnNodeClickAdd(node) {
            this.activeNode = node;
            if (this.activeNode.type == 'question') {
                this.newAnswer.uuid = uuid();
                console.log(this.activeNode.questionType);
                this.newAnswer.controlType = (this.activeNode.questionType == 1) ? 3 : 1;
                this.$refs.AddAnswerModal.show();
            } else {
                this.newQuestion.uuid = uuid();
                this.$refs.AddQuestionModal.show();
            }
        },
        resetObjs() {
            this.newQuestion = {
                                'uuid': '',
                                'text': '',
                                'type': 'question',
                                'questionType': 1,
                                'children': []
                            };
            this.newAnswer = {
                                'uuid': '',
                                'text': '',
                                'type': 'answer',
                                'controlType': '',
                                'children': []
                            };
        },
        doOnNodeClickDel(node) {
            this.deleteNode = node;
            this.$refs.ConfirmDelNodeModal.show();
        },
        doOnNodeClickEdit(node) {
            this.isEditing = true;
            this.activeNode = node;
            if (node.type == 'question') {
                this.copyObj(node, this.newQuestion);
                this.$refs.AddQuestionModal.show();
            } else {
                this.copyObj(node, this.newAnswer);
                this.$refs.AddAnswerModal.show();
            }
        },
        doOnDeleteNode() {
            this.$refs.ConfirmDelNodeModal.hide()
            this.tree.forEach((q, i) => {
                if (q.uuid === this.deleteNode.uuid) {
                    this.tree.splice(i, 1);
                } else {
                    this.nodeDelete(q, this.deleteNode);
                }
            });
            this.deleteNode = null;
        },
        addNewRootQuestion() {
            this.resetObjs();
            this.$refs.AddQuestionModal.show();
        },
        doOnAddNewQuestion() {
            if (this.isEditing) {
                console.log('isEditing');
                this.copyObj(this.newQuestion, this.activeNode);
                this.isEditing = false;
            } else {
                console.log('not isEditing');
                if (this.activeNode == null) {
                    this.tree.push(this.newQuestion);
                } else {
                    console.log(this.activeNode);
                    this.activeNode.children.push(this.newQuestion);
                }
            }
            this.activeNode = null;
            this.resetObjs();
        },
        doOnAddNewAnswer() {
            if (this.isEditing) {
                console.log('isEditing');
                this.copyObj(this.newAnswer, this.activeNode);
                this.isEditing = false;
            } else {
                console.log('not isEditing');
                console.log(this.activeNode);
                this.activeNode.children.push(this.newAnswer);
            }
            this.activeNode = null;
            this.resetObjs();
        },
        nodeDelete(rootNode, node) {
            rootNode.children.forEach((e, i) => {
                if (e.uuid === node.uuid) {
                    rootNode.children.splice(i, 1);
                    return;
                } else {
                    this.nodeDelete(e, node);
                }
            });
        },
        focusModalInput() {
            this.$refs.inputTextModal.focus();
        },
        doOnNodeClickCopy(node) {
            this.copyNode = node;
        },
        doOnNodeClickPaste(node) {
            node.children.push(this.copyNode);
            this.resetObjs();
        },
        copyObj(from, to) {
           for (let p in from) {
               to[p] = from[p];
           } 
        }
    }    
}
